  selector: '[uiDropdown]'
})
export class DropdownDirective {
  showing = false
  constructor(private el: ElementRef) {}

  @HostListener('click')
  toggle = (): void => {
    this.showing = !this.showing
  }

  isShowing = (): boolean => {
    return this.showing
  }
}

